/**
 * Composant principal de l'application Pokémon.
 * 
 * Ce composant utilise l'API Pokémon pour récupérer les données d'un Pokémon
 * en fonction de son numéro. Il affiche le nom, le poids et les capacités du Pokémon.
 * 
 * @example
 * <App />
 */
import axios from 'axios';
import { useEffect, useState } from 'react';
import './App.css';
import './Components/style.css';
import Main from './Components/Main';
import Favorites from './Components/Favorites';


/**
 * Fonction principale du composant App.
 * 
 * @returns {JSX.Element} Le composant App.
 */
function App() {

  /**
   * État du composant : données du Pokémon.
   * 
   * @type {object}
   */
  const [data, setData] = useState();

  /**
   * État du composant : nom du Pokémon.
   * 
   * @type {string}
   */
  const [name, setName] = useState();

  /**
   * État du composant : poids du Pokémon.
   * 
   * @type {number}
   */
  const [weight, setWeight] = useState();

  /**
   * État du composant : numéro du Pokémon.
   * 
   * @type {number}
   */
  const [number, setNumber] = useState(1);





  /**
   * URL de l'API Pokémon pour récupérer les données d'un Pokémon.
   * 
   * @type {string}
   */
  const URL = `https://pokeapi.co/api/v2/pokemon/${number}`;

  /**
   * Effet secondaire pour récupérer les données du Pokémon lorsque le numéro change.
   * 
   * @param {number} number Le numéro du Pokémon.
   */
  useEffect(()=>{
    axios.get(URL).then((response)=> {
      console.log(response.data)
      setData(response.data)
      setName(response.data.name)
      setWeight(response.data.weight)
      setNumber(response.data.id)
    }).catch((err)=>{
      window.alert(err);
    }) 
  },[number])

  return (
    <div className="App">
      <h1>Pokemon</h1>
      <input type="number" onChange={(e)=>{setNumber(e.target.value)}} />
      <button>Voir</button>
      <h2>{name}</h2>
      <h3>Force : {weight}</h3>
      <img src={data?data.sprites.other.dream_world.front_default : 
        "<p>Loading</p>"}/>
      <p>Mes abilitées :</p>
      {data ? data.abilities.map((value, key)=>{
        return(
          <div key={key}>
            {value.ability.name}
          </div>
        )
      }) : ""}
      <Main/>
    </div>
  );
}


export default App;
