import axios from 'axios';
import { useEffect, useState } from 'react';
import './App.css';
import './Components/style.css';
import Main from './Components/Main';
import { Link, Route, Switch } from 'react-router-dom';
import FavoritesPage from './Pages/FavoritesPage';

function App() {

  const [data, setData] = useState();
  const [name, setName] = useState();
  const [weight, setWeight] = useState();
  const [number, setNumber] = useState(1);
  const [favorites, setFavorites] = useState([]); // État pour stocker les favoris

  // URL de l'API Pokémon
  const URL = `https://pokeapi.co/api/v2/pokemon/${number}`;

  useEffect(()=>{
    axios.get(URL).then((response)=> {
      console.log(response.data)
      setData(response.data)
      setName(response.data.name)
      setWeight(response.data.weight)
      setNumber(response.data.id)
    }).catch((err)=>{
      window.alert(err);
    }) 
  },[number])

  // Fonction pour ajouter un Pokémon aux favoris
  const addToFavorites = () => {
    if (!favorites.includes(data)) {
      setFavorites([...favorites, data]);
    }
  };

  return (
    <div className="App">
      <h1>Pokemon</h1>
      <input type="number" onChange={(e)=>{setNumber(e.target.value)}} />
      <button>Voir</button>
      <h2>{name}</h2>
      <h3>Force : {weight}</h3>
      <img src={data?data.sprites.other.dream_world.front_default : 
        "<p>Loading</p>"}/>
      <div className="abilities">
        <h4>Mes abilitées :</h4>
        <ul>
          {data ? data.abilities.map((value, key)=>{
            return(
              <li key={key}>
                <span className="ability-name">{value.ability.name}</span>
                <p className="ability-description">{value.ability.description}</p>
              </li>
            )
          }) : ""}
        </ul>
      </div>
      <button onClick={addToFavorites}>Capture ce pokemon</button>
      <Main/>
      <Link to="/favorites">Voir les favoris</Link>
      <Switch>
        <Route path="/favorites" component={() => <FavoritesPage favorites={favorites} />} />
      </Switch>
    </div>
  );
}

export default App;
